<template>
  <div class="dark-theme-toggler">
    <div class="toggle" :class="{checked: dark}" @click="handleChange">
      <div class="toggle-track">
        <div class="toggle-track-check">
          <img
            src="data:image/png;base64,"
            width="16"
            height="16"
            role="presentation"
            style="pointer-events: none;"
          />
        </div>
        <div class="toggle-track-x">
          <img
            src="data:image/png;base64,"
            width="16"
            height="16"
            role="presentation"
            style="pointer-events: none;"
          />
        </div>
      </div>
      <div class="toggle-thumb"></div>
    </div>
    <input
      type="checkbox"
      class="toggler-screen-reader-only"
      :checked="dark"
      aria-label="Switch between Dark and Default theme"
    />
  </div>
</template>

<script>
export default {
  data() {
    const themeStore = localStorage.getItem('docute:theme')
    const dark =
      'dark' in this.$route.query
        ? true
        : themeStore === 'dark'
        ? true
        : themeStore === 'default'
        ? false
        : this.$store.getters.config.theme === 'dark'
    return {
      dark
    }
  },

  created() {
    this.$store.commit('SET_THEME', this.dark ? 'dark' : 'default')
  },

  methods: {
    handleChange() {
      const prevTheme = this.$store.getters.config.theme
      this.dark = !this.dark
      this.$store.commit(
        'SET_THEME',
        this.dark ? 'dark' : prevTheme === 'dark' ? 'default' : prevTheme
      )
      localStorage.setItem('docute:theme', this.dark ? 'dark' : 'default')
    }
  }
}
</script>

<style scoped>
.dark-theme-toggler {
  display: flex;
  align-items: center;
  height: 100%;

  @media screen and (max-width: 768px) {
    padding: 0 20px;
  }

  @nest [data-position='sidebar:post-end'] & {
    padding: 0 20px;
  }
}

.toggle {
  touch-action: pan-x;
  display: inline-block;
  position: relative;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  padding: 0;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
}

.toggle-track {
  width: 50px;
  height: 22px;
  padding: 0;
  border-radius: 30px;
  background-color: #0f1114;
  transition: all 0.2s ease;
}

.toggle-track-check {
  position: absolute;
  width: 17px;
  height: 17px;
  left: 5px;
  top: 0;
  bottom: 0;
  margin-top: auto;
  margin-bottom: auto;
  line-height: 0;
  opacity: 0;
  transition: opacity 0.25s ease;
}

.toggle-track-x {
  position: absolute;
  width: 17px;
  height: 17px;
  right: 5px;
  top: 0;
  bottom: 0;
  margin-top: auto;
  margin-bottom: auto;
  line-height: 0;
}

.toggle-thumb {
  position: absolute;
  top: 2px;
  left: 2px;
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background-color: white;
  box-sizing: border-box;
  transition: all 0.5s cubic-bezier(0.23, 1, 0.32, 1) 0ms;
  transform: translateX(0);
}

.checked .toggle-track-check,
.toggle-track-x {
  opacity: 1;
  transition: opacity 0.25s ease;
}

.checked .toggle-track-x {
  opacity: 0;
}

.checked .toggle-thumb {
  transform: translateX(26px);
  border-color: #19ab27;
}

.toggler-screen-reader-only {
  width: 1px;
  height: 1px;
  clip: rect(0 0 0 0);
  position: absolute;
  overflow: hidden;
  border: none;
  margin: -1px;
}
</style>
